-- Create trigger statement

CREATE OR REPLACE TRIGGER swt_message_id
  BEFORE INSERT ON message
  REFERENCING NEW AS NEW OLD AS OLD
  FOR EACH ROW
when (NEW.id is null)
BEGIN
  SELECT message_id_seq.NEXTVAL INTO :NEW.id FROM DUAL;
END;
/

EXIT;

